package com.audi.leetcode.two.pointer;

import java.util.HashMap;
import java.util.LinkedList;
import java.util.List;
import java.util.Map;

/**
 * https://leetcode.com/problems/find-all-anagrams-in-a-string/description/?envType=study-plan&id=level-1
 * <p>
 * 给定两个字符串p、q，求q的「相同字母异序词」在p出现的所有起始位置
 * <p>
 * Input: s = "cbaebabacd", p = "abc"
 * Output: [0,6]
 * Explanation:
 * The substring with start index = 0 is "cba", which is an anagram of "abc".
 * The substring with start index = 6 is "bac", which is an anagram of "abc".
 * <p>
 * Input: s = "abab", p = "ab"
 * Output: [0,1,2]
 * Explanation:
 * The substring with start index = 0 is "ab", which is an anagram of "ab".
 * The substring with start index = 1 is "ba", which is an anagram of "ab".
 * The substring with start index = 2 is "ab", which is an anagram of "ab".
 * <p>
 * 使用滑动窗口+hash表的思想解决
 *
 * @author : wangquanzhou
 * @date : 2023/3/28 20:25
 */
public class FindAllAnagramsinaString {

    public List<Integer> findAnagrams(String s, String p) {
        List<Integer> res = new LinkedList<>();
        if (p.length() > s.length()) {
            return res;
        }
        Map<Character, Integer> mapP = new HashMap<>(p.length());
        Map<Character, Integer> mapS = new HashMap<>(p.length());
        for (int i = 0; i < p.length(); i++) {
            mapP.put(p.charAt(i), mapP.getOrDefault(p.charAt(i), 0) + 1);
            mapS.put(s.charAt(i), mapS.getOrDefault(s.charAt(i), 0) + 1);
        }
        if (mapEquals(mapS, mapP)) {
            res.add(0);
        }
        for (int i = 1; i < s.length() - p.length() + 1; i++) {
            char front = s.charAt(i - 1);
            char end = s.charAt(i + p.length() - 1);
            if (mapS.get(front) == 1) {
                mapS.remove(front);
            } else {
                mapS.put(front, mapS.get(front) - 1);
            }
            mapS.put(end, mapS.getOrDefault(end, 0) + 1);
            if (mapEquals(mapS, mapP)) {
                res.add(i);
            }
        }
        return res;
    }

    private boolean mapEquals(Map<Character, Integer> a, Map<Character, Integer> b) {
        if (a.size() != b.size()) {
            return false;
        }
        for (Map.Entry<Character, Integer> entry : a.entrySet()) {
            // 注意这里要使用equals，而不是==，因为value是Integer包装类，在大约128的情况下==不会相等
            if (!entry.getValue().equals(b.get(entry.getKey()))) {
                return false;
            }
        }
        return true;
    }

    public static void main(String[] args) {
        FindAllAnagramsinaString anagramsinaString = new FindAllAnagramsinaString();
        System.out.println(anagramsinaString.findAnagrams("",
                ""));
    }
}
